import { FillIconProps } from ".";

export const FillIconTemplate = (props: FillIconProps) => {
  switch (props.fillType) {
    case "house":
      return <HouseIcon {...props} />;
    case "sparkle":
      return <SparkleIcon {...props} />;
    case "door":
      return <DoorIcon {...props} />;
    case "pause":
      return <PauseIcon {...props} />;
    case "play":
      return <PlayIcon {...props} />;
    case "heart":
      return <HeartIcon {...props} />;
    case "upload":
      return <UploadIcon {...props} />;
    case "calendar":
      return <CalendarIcon {...props} />;
    case "guidelines":
      return <GuidelinesIcon {...props} />;
    case "doublebed":
      return <DoubleBedIcon {...props} />;
    case "distancing":
      return <DistancingIcon {...props} />;
    case "check":
      return <CheckIcon {...props} />;
    case "smokeAlarm":
      return <SmokeAlarmIcon {...props} />;
    case "tv":
      return <TVIcon {...props} />;
    case "kitchen":
      return <KitchenIcon {...props} />;
    case "heating":
      return <HeatingIcon {...props} />;
    case "privateEntrance":
      return <PrivateEntranceIcon {...props} />;
    case "carbonMonoxide":
      return <CarbonMonoxideAlarmIcon {...props} />;
    case "people":
      return <PeopleIcon {...props} />;
    case "language":
      return <LanguageIcon {...props} />;
    case "computer":
      return <ComputerIcon {...props} />;
    case "smile":
      return <SmileIcon {...props} />;
    case "activity":
      return <ActivityIcon {...props} />;
    default:
      throw new Error("Invalid fillType provided");
  }
};

