import { useState } from "react";

/** styles **/
import shape from "@styles/shape.module.scss";
import color from "@styles/color.module.scss";
import space from "@styles/space.module.scss";
import layout from "@styles/layout.module.scss";
import font from "@styles/font.module.scss";
import input from "@input/input.module.scss";

/** Styling */
import { styleInput, styleLabel, styleContainer } from "./styling.text";

/** components */
import { Button } from "@button";

/** Helper */
import { checkPassword } from "@helper/auth";

/** stories */
import { Underline } from "@button/button.stories";

/**
 * Renders the text input component
 * @param {string} name - Type of input
 * @param {string} placeholder - Placeholder
 * @param handleChange
 * @param {string} value - Current value of the input
 * @param {string} direction - direction in which the input if attached to another
 * @param {string} inputType - Whether if the input is text-based or select-based
 */
export const PasswordInput: React.FC<{
  handleChange?: any;
  value?: string;
  direction?: string;
  errors?: boolean;
}> = ({ handleChange, value, direction, errors = false }) => {
  const [fieldActive, setFieldActive] = useState(false);
  const [hide, setHide] = useState(true);

  const activateField = () => {
    setFieldActive(true);
  };

  const deactivateField = () => {
    setFieldActive(false);
  };

  const renderShape = () => {
    switch (direction) {
      case "top":
        return [
          color["b-b--white__3"],
          color["b-l--white__3"],
          color["b-r--white__3"],
          shape["bbr--10"],
        ].join(" ");
      case "bottom":
        return [
          color["b-t--white__3"],
          color["b-l--white__3"],
          color["b-r--white__3"],
          shape["btr--10"],
        ].join(" ");
      default:
        return [color["b--white__3"], shape["br--10"]].join(" ");
    }
  };

  return (
    <div
      style={{ height: 60 }}
      className={`${[
        input["outside"],
        layout["relative"],
        space["p-v--6"],
        space["p-h--12"],
        layout["flex"],
        layout["items-center"],
      ].join(" ")} ${renderShape()} ${styleContainer(
        errors,
        fieldActive,
        checkPassword(value)
      )}`}
    >
      <div
        style={{
          position: "relative",
          height: "100%",
          width: "100%",
        }}
      >
        <input
          autoFocus={true}
          id="password"
          name="password"
          type={hide ? "password" : "text"}
          onChange={handleChange}
          value={value}
          onFocus={activateField}
          onBlur={deactivateField}
          className={`${[
            space["p--0"],
            shape["w--full"],
            layout["block"],
            color["b--0"],
            font["size--16"],
            font["weight--300"],
            color["c__placeholder--black"],
            input["input"],
          ].join(" ")} ${styleInput(
            errors,
            fieldActive,
            checkPassword(value)
          )}`}
          placeholder={fieldActive ? "Password" : undefined}
        />
        <label
          htmlFor="password"
          className={`${[
            layout["absolute"],
            font["size--12"],
            color["c--gray__1"],
            font["weight--100"],
            input["label"],
          ].join(" ")} ${styleLabel(
            errors,
            fieldActive,
            checkPassword(value),
            value
          )}`}
        >
          Password
        </label>
      </div>
      <div
        className={[
          layout["flex"],
          layout["items-center"],
          layout["justify-center"],
          space["m-l--18"],
        ].join(" ")}
      >
        <Button
          {...Underline.args}
          onClick={() => setHide((prevHide) => !prevHide)}
          font={13}
          title={hide ? "Show" : "Hide"}
        />
      </div>
    </div>
  );
};
