import React from "react";
import { Story, Meta } from "@storybook/react/types-6-0";

/** components */
import { Button, ButtonProps } from "@button";

/** styles */
import button from "@button/button.module.scss";
import font from "@styles/font.module.scss";
import color from "@styles/color.module.scss";
import layout from "@styles/layout.module.scss";
import shape from "@styles/shape.module.scss";
import space from "@styles/space.module.scss";
import animation from "@styles/animation.module.scss";

export default {
  title: "Atoms/Button",
  component: Button,
  argTypes: {
    inverse: {
      control: "boolean",
    },
    variant: {
      control: { disable: true },
    },
    icon: {
      control: { disable: true },
    },
    to: {
      control: { disable: true },
    },
    extendsTo: {
      control: { disable: true },
    },
  },
} as Meta;

const disableInverse = {
  inverse: {
    control: {
      disable: true,
    },
  },
};

const disableTitle = {
  title: {
    control: {
      disable: true,
    },
  },
};

const TemplateStory: Story<ButtonProps> = (args) => <Button {...args} />;

export const Transparent = TemplateStory.bind({});
Transparent.args = {
  variant: "transparent",
  content: <h3 className={[font["size--15"]].join(" ")}>Button</h3>,
};

export const Globe = TemplateStory.bind({});
Globe.args = {
  variant: "globe",
  extendsTo: [font["text--left"]].join(" "),
};
Globe.argTypes = {
  selected: {
    control: "boolean",
  },
};

export const Menu = TemplateStory.bind({});
Menu.args = {
  variant: "menu",
  extendsTo: [animation["hover-shadow--lg"], shape["br--30"]].join(" "),
};
Menu.argTypes = {
  ...disableTitle,
  user: {
    control: "boolean",
  },
};

export const Privacy = TemplateStory.bind({});
Privacy.args = {
  variant: "privacy",
};

Privacy.decorators = [
  (Story) => (
    <div style={{ width: 300 }}>
      <Story />
    </div>
  ),
];

export const Border = TemplateStory.bind({});
Border.args = {
  variant: "border",
  extendsTo: [color["bg--transparent"]].join(" "),
};
Border.argTypes = {
  size: {
    control: {
      type: "select",
      options: ["sm", "md", "lg"],
    },
  },
  bold: {
    control: "boolean",
  },
};

export const Banner = TemplateStory.bind({});
Banner.args = {
  variant: "banner",
  extendsTo: [color["bg--white"], shape["br--6"]].join(" "),
};
Banner.argTypes = {
  ...disableInverse,
};

Banner.decorators = [
  (Story) => (
    <div style={{ width: 200 }}>
      <Story />
    </div>
  ),
];

export const Primary = TemplateStory.bind({});
Primary.args = {
  variant: "primary",
};
Primary.decorators = [
  (Story) => (
    <div style={{ width: 300 }}>
      <Story />
    </div>
  ),
];
Primary.argTypes = {
  ...disableInverse,
  loading: {
    control: "boolean",
  },
};

export const Searchbar = TemplateStory.bind({});
Searchbar.args = {
  variant: "searchbar",
};
Searchbar.argTypes = {
  ...disableInverse,
  mini: {
    control: "boolean",
  },
};

export const Auth = TemplateStory.bind({});
Auth.args = {
  variant: "auth",
  extendsTo: [
    button["hover__auth"],
    color["bg--transparent"],
    layout["block"],
    shape["w--full"],
    space["p-h--12"],
    space["p-v--12"],
    shape["br--8"],
  ].join(" "),
};

Auth.decorators = [
  (Story) => (
    <div style={{ width: 300 }}>
      <Story />
    </div>
  ),
];

Auth.argTypes = {
  auth: {
    control: {
      type: "select",
      options: ["email", "facebook", "google", "apple"],
    },
  },
};

export const Paginate = TemplateStory.bind({});
Paginate.args = {
  variant: "paginate",
};
Paginate.argTypes = {
  direction: {
    control: {
      type: "select",
      options: ["left", "right"],
    },
  },
};

export const Option = TemplateStory.bind({});
Option.args = {
  variant: "option",
  extendsTo: [shape["w--full"]].join(" "),
};

export const Underline = TemplateStory.bind({});
Underline.args = {
  variant: "underline",
};

export const Filter = TemplateStory.bind({});
Filter.args = {
  variant: "filter",
};

export const Modal = TemplateStory.bind({});
Modal.args = {
  variant: "modal",
};
Modal.argTypes = {
  modalType: {
    control: {
      type: "select",
      options: ["close", "back"],
    },
  },
};

export const Back = TemplateStory.bind({});
Back.args = {
  variant: "back",
};

export const Link = TemplateStory.bind({});
Link.args = {
  variant: "link",
};

export const Search = TemplateStory.bind({});
Search.args = {
  variant: "search",
};
Search.argTypes = {
  expand: {
    control: "boolean",
  },
};

export const Expand = TemplateStory.bind({});
Expand.args = {
  variant: "expand",
};

export const Location = TemplateStory.bind({});
Location.args = {
  variant: "location",
};
Location.argTypes = {
  type: {
    control: {
      type: "select",
      options: ["explore", "recent"],
    },
  },
};

export const Nearby = TemplateStory.bind({});
Nearby.args = {
  variant: "nearby",
};
